INSERT INTO bs_voting_remark (remarkid, votingid, resourceid, useranony, remark, operatedate, operatetime) VALUES (732, 11, 2423, 0, '即开通班车，应该考虑方向的双向性：1.开通宁西工厂到新塘、广州的班车，2.开通宁西工厂到荔城的班车，3.晚上的时间应该相应的延迟.', '2014-11-07', '08:15:57');
