INSERT INTO bs_voting_remark (remarkid, votingid, resourceid, useranony, remark, operatedate, operatetime) VALUES (739, 11, 6, 0, '开车班车是非常有利于员工的生活及员工稳定', '2014-11-07', '09:41:06');
